import { cn } from "@/lib/utils";
import { Check, CheckCheck, Play, Link2, Eye, Download, Volume2 } from "lucide-react";
import { Avatar } from "./Avatar";
import { useState } from "react";

export interface MessageBubbleProps {
  id: string;
  content: string;
  timestamp: string;
  isOwn: boolean;
  senderName?: string;
  senderAvatar?: string;
  status?: "sent" | "delivered" | "read";
  showAvatar?: boolean;
  isFirstInGroup?: boolean;
  isLastInGroup?: boolean;
  replyTo?: {
    name: string;
    content: string;
  };
  media?: {
    type: "image" | "video" | "audio" | "link";
    url?: string;
    title?: string;
    description?: string;
    duration?: string;
    fileSize?: string;
    views?: number;
  } | Array<{
    type: "image" | "video";
    url?: string;
    duration?: string;
    fileSize?: string;
    views?: number;
  }>;
}

export function MessageBubble({
  id,
  content,
  timestamp,
  isOwn,
  senderName,
  senderAvatar,
  status,
  showAvatar = false,
  isFirstInGroup = true,
  isLastInGroup = true,
  replyTo,
  media,
}: MessageBubbleProps) {
  return (
    <div
      className={cn(
        "flex gap-2",
        isOwn ? "justify-end pr-4" : "justify-start",
        isLastInGroup ? "mb-2" : "mb-0.5"
      )}
      data-testid={`message-${id}`}
    >
      {!isOwn && (
        <div className={cn(
          "flex-shrink-0 w-8 flex items-start pt-1",
          !showAvatar && "opacity-0"
        )}>
          {showAvatar ? (
            <Avatar src={senderAvatar} name={senderName || "User"} size="sm" />
          ) : (
            <div className="w-8 h-8" />
          )}
        </div>
      )}
      <div
        className={cn(
          "flex flex-col",
          isOwn ? "max-w-xs sm:max-w-sm md:max-w-md" : "flex-1 max-w-xs sm:max-w-sm md:max-w-md"
        )}
      >
        {isFirstInGroup && !isOwn && senderName && (
          <span className="text-xs font-medium text-primary mb-1 block">
            {senderName}
          </span>
        )}
        {Array.isArray(media) && media.length > 0 && (
          <div className="grid gap-1 mb-1" style={{ gridTemplateColumns: media.length === 1 ? '1fr' : media.length === 2 ? '1fr 1fr' : '1fr 1fr 1fr' }}>
            {media.map((item, idx) => (
              <div key={idx} className="relative rounded-lg overflow-hidden group" style={{ aspectRatio: '1/1' }}>
                <img
                  src={item.url}
                  alt={`Media ${idx + 1}`}
                  className="w-full h-full object-cover"
                />
                {item.type === "video" && (
                  <>
                    <div className="absolute inset-0 flex items-center justify-center bg-black/10 group-hover:bg-black/30 transition-colors">
                      <Play className="w-8 h-8 text-primary-foreground fill-primary-foreground opacity-80" />
                    </div>
                    {item.duration && (
                      <div className="absolute bottom-2 left-2 px-1.5 py-0.5 rounded-md bg-black/60 text-xs text-white font-medium">
                        {item.duration}
                      </div>
                    )}
                  </>
                )}
                {item.views !== undefined && (
                  <div className="absolute bottom-2 right-2 flex items-center gap-0.5 px-1.5 py-0.5 rounded-md bg-black/60 text-xs text-white">
                    <Eye className="w-3 h-3" />
                    {item.views}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {!Array.isArray(media) && (media?.type === "image" || media?.type === "video") && media?.url && (
          <>
            {media.type === "image" && (
              <div className="rounded-xl overflow-hidden mb-1">
                <img 
                  src={media.url} 
                  alt="Message image"
                  className="w-full max-h-80 object-cover rounded-xl"
                />
              </div>
            )}
            {media.type === "video" && (
              <div className="relative rounded-xl overflow-hidden mb-1" style={{ aspectRatio: '9/16', maxWidth: '100%' }}>
                <img
                  src={media.url}
                  alt="Video thumbnail"
                  className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 flex items-center justify-center bg-black/10 group-hover:bg-black/30 transition-colors">
                  <div className="flex items-center justify-center w-16 h-16 rounded-full bg-primary hover:bg-primary/80 transition-colors shadow-lg">
                    <Play className="w-7 h-7 text-primary-foreground fill-primary-foreground" />
                  </div>
                </div>

                {media.duration && (
                  <div className="absolute top-3 left-3 px-2 py-1 rounded-md bg-black/60 text-xs text-white font-medium">
                    {media.duration}
                  </div>
                )}

                {media.fileSize && (
                  <div className="absolute bottom-3 left-3 flex items-center gap-1 px-2 py-1 rounded-md bg-black/60 text-xs text-white">
                    <Download className="w-3 h-3" />
                    <span>{media.fileSize}</span>
                  </div>
                )}

                {media.views !== undefined && (
                  <div className="absolute bottom-3 right-3 flex items-center gap-1 px-2 py-1 rounded-md bg-black/60 text-xs text-white">
                    <Eye className="w-3 h-3" />
                    <span>{media.views}</span>
                  </div>
                )}
              </div>
            )}
          </>
        )}

        <div
          className={cn(
            "px-3 py-2 relative",
            isOwn
              ? "bg-primary text-primary-foreground rounded-tl-2xl rounded-tr-2xl rounded-bl-2xl"
              : !Array.isArray(media) && media?.type === "audio" && media?.url
              ? "bg-card border border-card-border rounded-3xl"
              : "bg-card border border-card-border rounded-tr-2xl rounded-br-2xl rounded-bl-2xl"
          )}
        >
          {replyTo && (
            <div
              className={cn(
                "mb-1.5 pl-2 border-l-2 text-xs",
                isOwn
                  ? "border-primary-foreground/50 text-primary-foreground/80"
                  : "border-primary text-muted-foreground"
              )}
            >
              <span className="font-medium block">{replyTo.name}</span>
              <span className="line-clamp-1">{replyTo.content}</span>
            </div>
          )}

          {!Array.isArray(media) && media?.type === "audio" && media?.url && (
            <div className="flex items-center gap-2.5 my-1">
              <button className={cn(
                "flex-shrink-0 w-9 h-9 rounded-full flex items-center justify-center transition-colors",
                isOwn
                  ? "bg-primary-foreground/40 hover:bg-primary-foreground/50"
                  : "bg-primary hover:bg-primary/90"
              )}>
                <Play className={cn(
                  "w-4 h-4 fill-current",
                  isOwn ? "text-primary-foreground" : "text-primary-foreground"
                )} />
              </button>

              <div className="flex-1 h-6 flex items-center">
                <svg className="w-full h-full" viewBox="0 0 100 24" preserveAspectRatio="none">
                  {[...Array(35)].map((_, i) => {
                    const height = Math.random() * 20 + 2;
                    return (
                      <rect
                        key={i}
                        x={i * 2.85}
                        y={(24 - height) / 2}
                        width="1.8"
                        height={height}
                        fill={isOwn ? "rgba(255,255,255,0.6)" : "rgba(42, 171, 238, 0.6)"}
                        rx="0.75"
                      />
                    );
                  })}
                </svg>
              </div>

              {media.duration && (
                <span className={cn(
                  "text-xs font-medium flex-shrink-0 min-w-fit",
                  isOwn ? "text-primary-foreground/80" : "text-muted-foreground"
                )}>
                  {media.duration}
                </span>
              )}

              <button className="flex-shrink-0 p-1 transition-colors">
                <Volume2 className={cn(
                  "w-4 h-4",
                  isOwn ? "text-primary-foreground/80" : "text-muted-foreground"
                )} />
              </button>
            </div>
          )}

          {!Array.isArray(media) && media?.type === "link" && media?.url && (
            <div className="mb-2">
              <a
                href={media.url}
                target="_blank"
                rel="noopener noreferrer"
                className={cn(
                  "block p-2 rounded-lg border",
                  isOwn
                    ? "bg-primary-foreground/10 border-primary-foreground/20"
                    : "bg-background border-card-border"
                )}
              >
                <div className="flex items-start gap-2">
                  <Link2 className="w-4 h-4 flex-shrink-0 mt-1" />
                  <div className="min-w-0">
                    {media.title && (
                      <p className="font-medium text-sm truncate">{media.title}</p>
                    )}
                    {media.description && (
                      <p className="text-xs opacity-75 truncate">{media.description}</p>
                    )}
                    <p className="text-xs opacity-50 truncate">{new URL(media.url).hostname}</p>
                  </div>
                </div>
              </a>
            </div>
          )}

          {content && (
            <p className="text-[15px] leading-relaxed whitespace-pre-wrap break-words">
              {content}
            </p>
          )}
          <div
            className={cn(
              "flex items-center justify-end gap-1 mt-1 -mb-0.5",
              isOwn ? "text-primary-foreground/70" : "text-muted-foreground"
            )}
          >
            <span className="text-[10px]">{timestamp}</span>
            {isOwn && status && (
              <span className="flex-shrink-0">
                {status === "read" ? (
                  <CheckCheck className="h-3.5 w-3.5" />
                ) : (
                  <Check className="h-3.5 w-3.5" />
                )}
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/layout/NewChatDial.content:
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Avatar } from "../chat/Avatar";
import { Search, Users, UserPlus, X, Check } from "lucide-react";
import { cn } from "@/lib/utils";

interface Contact {
  id: string;
  name: string;
  avatar?: string;
  isOnline?: boolean;
  lastSeen?: string;
}

interface NewChatDialogProps {
  open: boolean;
  onClose: () => void;
  contacts: Contact[];
  onSelectContact: (contactId: string) => void;
  onCreateGroup: (memberIds: string[], groupName: string) => void;
}

export function NewChatDialog({
  open,
  onClose,
  contacts,
  onSelectContact,
  onCreateGroup,
}: NewChatDialogProps) {
  const [search, setSearch] = useState("");
  const [mode, setMode] = useState<"chat" | "group">("chat");
  const [selectedMembers, setSelectedMembers] = useState<string[]>([]);
  const [groupName, setGroupName] = useState("");

  const filteredContacts = contacts.filter((contact) =>
    contact.name.toLowerCase().includes(search.toLowerCase())
  );

  const handleContactClick = (contactId: string) => {
    if (mode === "chat") {
      onSelectContact(contactId);
      onClose();
    } else {
      setSelectedMembers((prev) =>
        prev.includes(contactId)
          ? prev.filter((id) => id !== contactId)
          : [...prev, contactId]
      );
    }
  };

  const handleCreateGroup = () => {
    if (selectedMembers.length >= 2 && groupName.trim()) {
      onCreateGroup(selectedMembers, groupName.trim());
      onClose();
      setSelectedMembers([]);
      setGroupName("");
      setMode("chat");
    }
  };

  const resetState = () => {
    setSearch("");
    setMode("chat");
    setSelectedMembers([]);
    setGroupName("");
  };

  return (
    <Dialog
      open={open}
      onOpenChange={(isOpen) => {
        if (!isOpen) {
          resetState();
          onClose();
        }
      }}
    >
      <DialogContent className="sm:max-w-md p-0 gap-0">
        <DialogHeader className="p-4 border-b">
          <DialogTitle>
            {mode === "chat" ? "New Message" : "New Group"}
          </DialogTitle>
        </DialogHeader>

        <div className="p-4 border-b space-y-3">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search contacts..."
              className="pl-9"
              data-testid="input-search-contacts"
            />
          </div>

          {mode === "chat" && (
            <Button
              variant="ghost"
              className="w-full justify-start gap-3"
              onClick={() => setMode("group")}
              data-testid="button-new-group"
            >
              <div className="h-10 w-10 rounded-full bg-primary flex items-center justify-center">
                <Users className="h-5 w-5 text-primary-foreground" />
              </div>
              <span>New Group</span>
            </Button>
          )}

          {mode === "group" && (
            <div className="space-y-3">
              <Input
                value={groupName}
                onChange={(e) => setGroupName(e.target.value)}
                placeholder="Group name"
                data-testid="input-group-name"
              />
              {selectedMembers.length > 0 && (
                <div className="flex flex-wrap gap-2">
                  {selectedMembers.map((memberId) => {
                    const contact = contacts.find((c) => c.id === memberId);
                    if (!contact) return null;
                    return (
                      <span
                        key={memberId}
                        className="flex items-center gap-1 bg-accent rounded-full pl-1 pr-2 py-1"
                      >
                        <Avatar src={contact.avatar} name={contact.name} size="sm" />
                        <span className="text-xs">{contact.name.split(" ")[0]}</span>
                        <button
                          onClick={() =>
                            setSelectedMembers((prev) =>
                              prev.filter((id) => id !== memberId)
                            )
                          }
                          className="hover:bg-muted rounded-full p-0.5"
                        >
                          <X className="h-3 w-3" />
                        </button>
                      </span>
                    );
                  })}
                </div>
              )}
            </div>
          )}
        </div>

        <ScrollArea className="h-[300px]">
          <div className="p-2">
            {filteredContacts.map((contact) => (
              <button
                key={contact.id}
                onClick={() => handleContactClick(contact.id)}
                className={cn(
                  "w-full flex items-center gap-3 p-2 rounded-md hover-elevate active-elevate-2",
                  mode === "group" &&
                    selectedMembers.includes(contact.id) &&
                    "bg-accent"
                )}
                data-testid={`contact-${contact.id}`}
              >
                <Avatar
                  src={contact.avatar}
                  name={contact.name}
                  size="md"
                  isOnline={contact.isOnline}
                  showStatus
                />
                <div className="flex-1 text-left">
                  <span className="font-medium text-sm">{contact.name}</span>
                  <p className="text-xs text-muted-foreground">
                    {contact.isOnline ? "online" : contact.lastSeen || "offline"}
                  </p>
                </div>
                {mode === "group" && selectedMembers.includes(contact.id) && (
                  <span className="h-5 w-5 rounded-full bg-primary flex items-center justify-center">
                    <Check className="h-3 w-3 text-primary-foreground" />
                  </span>
                )}
              </button>
            ))}
          </div>
        </ScrollArea>

        {mode === "group" && (
          <div className="p-4 border-t">
            <Button
              onClick={handleCreateGroup}
              disabled={selectedMembers.length < 2 || !groupName.trim()}
              className="w-full"
              data-testid="button-create-group"
            >
              Create Group ({selectedMembers.length} members)
            </Button>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/chat/Avatar.tsx.content:
import { Avatar as ShadAvatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { cn } from "@/lib/utils";

interface AvatarProps {
  src?: string;
  name: string;
  size?: "sm" | "md" | "lg" | "xl";
  isOnline?: boolean;
  showStatus?: boolean;
  className?: string;
}

const sizeClasses = {
  sm: "h-8 w-8",
  md: "h-10 w-10",
  lg: "h-12 w-12",
  xl: "h-14 w-14",
};

const statusSizeClasses = {
  sm: "h-2 w-2",
  md: "h-2.5 w-2.5",
  lg: "h-3 w-3",
  xl: "h-3.5 w-3.5",
};

function getInitials(name: string): string {
  return name
    .split(" ")
    .map((n) => n[0])
    .join("")
    .toUpperCase()
    .slice(0, 2);
}

function getAvatarColor(name: string): string {
  const colors = [
    "bg-red-500",
    "bg-orange-500",
    "bg-amber-500",
    "bg-yellow-500",
    "bg-lime-500",
    "bg-green-500",
    "bg-emerald-500",
    "bg-teal-500",
    "bg-cyan-500",
    "bg-sky-500",
    "bg-blue-500",
    "bg-indigo-500",
    "bg-violet-500",
    "bg-purple-500",
    "bg-fuchsia-500",
    "bg-pink-500",
    "bg-rose-500",
  ];
  const index = name.charCodeAt(0) % colors.length;
  return colors[index];
}

export function Avatar({
  src,
  name,
  size = "md",
  isOnline = false,
  showStatus = false,
  className,
}: AvatarProps) {
  return (
    <div className="relative inline-block">
      <ShadAvatar className={cn(sizeClasses[size], className)}>
        <AvatarImage src={src} alt={name} />
        <AvatarFallback className={cn(getAvatarColor(name), "text-white font-medium")}>
          {getInitials(name)}
        </AvatarFallback>
      </ShadAvatar>
      {showStatus && (
        <span
          className={cn(
            "absolute bottom-0 right-0 rounded-full border-2 border-background",
            statusSizeClasses[size],
            isOnline ? "bg-status-online" : "bg-status-offline"
          )}
          data-testid={`status-indicator-${isOnline ? "online" : "offline"}`}
        />
      )}
    </div>
  );
}

